private HttpURLConnection openConnection() throws IOException {
  if (!isTraceEnabled) {
    return (HttpURLConnection) url.openConnection();
  }
  long start = System.nanoTime();
  try {
    return (HttpURLConnection) url.openConnection();
  } finally {
    connectionTimeMs = elapsedTimeMs(start);
  }
}
