static void sendingRequest(AbfsRestOperationType operationType) {
  if (!isAutoThrottlingEnabled) {
    return;
  }

  switch (operationType) {
    case ReadFile:
      singleton.readThrottler.suspendIfNecessary();
      break;
    case Append:
      singleton.writeThrottler.suspendIfNecessary();
      break;
    default:
      break;
  }
}
