public T validate(final String configValue) throws InvalidConfigurationValueException {
  if (configValue == null) {
    if (this.throwIfInvalid) {
      throw new InvalidConfigurationValueException(this.configKey);
    }
    return this.defaultVal;
  }
  return null;
}
