public void trimWrite(int delta) {
  Preconditions.checkState(delta < count);
  if (LOG.isDebugEnabled()) {
    LOG.debug("Trim write request by delta:" + delta + " " + toString());
  }
  synchronized(this) {
    trimDelta = delta;
    if (originalCount == INVALID_ORIGINAL_COUNT) {
      originalCount = count;
    }
    trimData();
  }
}
