public static synchronized void fileSystemClosed() {
  if (instance != null) {
    instance.publishMetricsNow();
  }
  if (numFileSystems == 1) {
    instance.stop();
    instance.shutdown();
    instance = null;
  }
  numFileSystems--;
}
