public synchronized int available() throws IOException {
  if (isPageBlob) {
    return in.available();
  } else {
    if (closed) {
      throw new IOException("Stream closed");
    }
    final long remaining = this.fileLength - pos;
    return remaining <= Integer.MAX_VALUE ?
        (int) remaining : Integer.MAX_VALUE;
  }
}
