private static String removeTrailingSlash(String key) {
  if (key.length() == 0 || key.length() == 1) {
    return key;
  }
  if (key.charAt(key.length() - 1) == '/') {
    return key.substring(0, key.length() - 1);
  } else {
    return key;
  }
}
