private FileMetadata updateFileStatusPath(FileMetadata meta, Path path) {
  meta.setPath(path.makeQualified(getUri(), getWorkingDirectory()));
  // reduce memory use by setting the internal-only key to null
  meta.removeKey();
  return meta;
}
