    String groupName, int mode, int mask) {
  if ((mode & mask) != 0) {
    return true;
  }
  if (((mode & (mask << 6)) != 0) 
      && (getUser().equals(userName))) {
    return true;
  }
  if (((mode & (mask << 6)) != 0) 
      && (containsGroup(groupName))) {
    return true;
  }
  return false;
}
