private String generateOlderVersionBlockId(long id) {

  byte[] blockIdInBytes = new byte[8];
  for (int m = 0; m < 8; m++) {
    blockIdInBytes[7 - m] = (byte) ((id >> (8 * m)) & 0xFF);
  }

  return new String(
          Base64.encodeBase64(blockIdInBytes),
          StandardCharsets.UTF_8);
}

