private synchronized void flushIOBuffers()  {
  if (outBuffer.size() == 0) {
    return;
  }
  lastQueuedTask = new WriteRequest(outBuffer.toByteArray());
  ioThreadPool.execute(lastQueuedTask);
  outBuffer = new ByteArrayOutputStream();
}
