    final StorageType t = i.next();
    if (expected.remove(t)) {
      i.remove();
    }
  }
  if (ignoreNonMovable) {
    removeNonMovable(existing);
    removeNonMovable(expected);
  }
  return expected.isEmpty() || existing.isEmpty();
}

