public synchronized void stop() {
  if (kdc != null) {
    System.getProperties().remove("java.security.krb5.conf");
    System.getProperties().remove("sun.security.krb5.debug");
    kdc.stop();
    try {
      ds.shutdown();
    } catch (Exception ex) {
      LOG.error("Could not shutdown ApacheDS properly: {}", ex.toString(),
              ex);
    }
  }
  delete(workDir);
}
