private void delete(File f) {
  if (f.isFile()) {
    if (! f.delete()) {
      LOG.warn("WARNING: cannot delete file " + f.getAbsolutePath());
    }
  } else {
    File[] fileList = f.listFiles();
    if (fileList != null) {
      for (File c : fileList) {
        delete(c);
      }
    }
    if (! f.delete()) {
      LOG.warn("WARNING: cannot delete directory " + f.getAbsolutePath());
    }
  }
}

