private void logEvent(AuditEvent event) {
  AUDIT_LOG.info(
      "OK[op={}, key={}, user={}, accessCount={}, interval={}ms] {}",
      event.getOp(), event.getKeyName(), event.getUser(),
      event.getAccessCount().get(),
      (System.currentTimeMillis() - event.getStartTime()),
      event.getExtraMsg());
}
