private static int readLength(ByteBuffer bb) {
  int length = bb.get();
  if ((length & (byte)0x80) != 0) {
    int varlength = length & 0x7f;
    length = 0;
    for (int i=0; i < varlength; i++) {
      length = (length << 8) | (bb.get() & 0xff);
    }
  }
  return length;
}
