public synchronized XDR unset(int xid, XDR in, XDR out) {
  PortmapMapping mapping = PortmapRequest.mapping(in);
  synchronized(this) {
    map.remove(PortmapMapping.key(mapping));
  }
  return PortmapResponse.booleanReply(out, xid, true);
}
