public void stop() {
  if (udpBoundPort > 0) {
    rpcProgram.unregister(PortmapMapping.TRANSPORT_UDP, udpBoundPort);
    udpBoundPort = 0;
  }
  if (tcpBoundPort > 0) {
    rpcProgram.unregister(PortmapMapping.TRANSPORT_TCP, tcpBoundPort);
    tcpBoundPort = 0;
  }
}
