public CacheEntry checkOrAddToCache(InetAddress clientId, int xid) {
  ClientRequest req = new ClientRequest(clientId, xid);
  CacheEntry e;
  synchronized(map) {
    e = map.get(req);
    if (e == null) {
      // Add an inprogress cache entry
      map.put(req, new CacheEntry());
    }
  }
  return e;
}
