public XDR write(XDR xdr) {
  xdr.writeInt(xid);
  xdr.writeInt(messageType.getValue());
  xdr.writeInt(replyState.getValue());
  Verifier.writeFlavorAndVerifier(verifier, xdr);
  xdr.writeInt(acceptState.getValue());
  return xdr;
}
