private void doUpgrade(NNStorage storage) throws IOException {
  for (Iterator<StorageDirectory> it = storage.dirIterator(false);
       it.hasNext();) {
    StorageDirectory sd = it.next();
    NNUpgradeUtil.doUpgrade(sd, storage);
  }
}
