public XDR writeHeaderAndResponse(XDR out, int xid, Verifier verifier) {
  RpcAcceptedReply reply = RpcAcceptedReply.getAcceptInstance(xid, verifier);
  reply.write(out);
  out.writeInt(this.getStatus());
  return out;
}
