public void stop() {
  if (nfsBoundPort > 0) {
    rpcProgram.unregister(PortmapMapping.TRANSPORT_TCP, nfsBoundPort);
    nfsBoundPort = 0;
  }
  rpcProgram.stopDaemons();
}
