private long bytesToLong(byte[] data) {
  ByteBuffer buffer = ByteBuffer.allocate(8);
  for (int i = 0; i < 8; i++) {
    buffer.put(data[i]);
  }
  buffer.flip();// need flip
  return buffer.getLong();
}
