public boolean deserialize(XDR xdr) {
  if (!XDR.verifyLength(xdr, 32)) {
    return false;
  }
  int size = xdr.readInt();
  handle = xdr.readFixedOpaque(size);
  fileId = bytesToLong(handle);
  return true;
}
