public HttpURLConnection configure(HttpURLConnection conn)
        throws IOException {
  if (cc != null) {
    conn = cc.configure(conn);
  }
  conn.setConnectTimeout(timeout * 1000);  // conversion to milliseconds
  conn.setReadTimeout(timeout * 1000);
  return conn;
}
