public void close() throws IOException {
  for (KMSClientProvider provider : providers) {
    try {
      provider.close();
    } catch (IOException ioe) {
      LOG.error("Error closing provider with url"
          + "[" + provider.getKMSUrl() + "]");
    }
  }
}
