  try {
    Runnable e;
    while ((e = queue.deleteByName(keyName)) != null) {
      executor.remove(e);
    }
    writeLock(keyName);
    try {
      keyQueues.get(keyName).clear();
    } finally {
      writeUnlock(keyName);
    }
  } catch (ExecutionException ex) {
    //NOP
  }
}

