synchronized protected int next(int nbits) {
  fillReservoir(4);
  int n = reservoir[pos] |
      (reservoir[pos + 1] << 8) |
      (reservoir[pos + 2] << 16) |
      (reservoir[pos + 3] << 24);
  pos += 4;
  return n & (0xffffffff >> (32 - nbits));
}
