private static BindException constructBindException(ServerConnector listener,
    BindException ex) {
  BindException be = new BindException("Port in use: "
      + listener.getHost() + ":" + listener.getPort());
  if (ex != null) {
    be.initCause(ex);
  }
  return be;
}
