private Map<String, String> getDefaultHeaders() {
  Map<String, String> headers = new HashMap<>();
  String[] splitVal = X_CONTENT_TYPE_OPTIONS.split(":");
  headers.put(HTTP_HEADER_PREFIX + splitVal[0],
          splitVal[1]);
  splitVal = X_XSS_PROTECTION.split(":");
  headers.put(HTTP_HEADER_PREFIX + splitVal[0],
          splitVal[1]);
  return headers;
}
