private void closeConnection() {
  if (socket == null) {
    return;
  }
  // close the current connection
  try {
    socket.close();
  } catch (IOException e) {
    LOG.warn("Not able to close a socket", e);
  }
  // set socket to null so that the next call to setupIOstreams
  // can start the process of connect all over again.
  socket = null;
}

