private boolean queueIsReallyEmpty(BlockingQueue<?> q) {
  for (int i = 0; i < CHECKPOINT_NUM; i++) {
    try {
      Thread.sleep(CHECKPOINT_INTERVAL_MS);
    } catch (InterruptedException ie) {
      return false;
    }
    if (!q.isEmpty()) {
      return false;
    }
  }
  return true;
}

