private boolean queueIsReallyEmpty(BlockingQueue q) {
  boolean wasEmpty = q.isEmpty();
  try {
    Thread.sleep(10);
  } catch (InterruptedException ie) {
    return false;
  }
  return q.isEmpty() && wasEmpty;
}
