private int[] getDefaultQueueWeights(int aNumQueues) {
  int[] weights = new int[aNumQueues];

  int weight = 1; // Start low
  for(int i = aNumQueues - 1; i >= 0; i--) { // Start at lowest queue
    weights[i] = weight;
    weight *= 2; // Double every iteration
  }
  return weights;
}
