void decrUserConnections(String user) {
  synchronized (userToConnectionsMapLock) {
    Integer count = userToConnectionsMap.get(user);
    if (count == null) {
      return;
    } else {
      count--;
    }
    if (count == 0) {
      userToConnectionsMap.remove(user);
    } else {
      userToConnectionsMap.put(user, count);
    }
  }
}
