private boolean offerQueues(int priority, E e, boolean includeLast) {
  int lastPriority = queues.size() - (includeLast ? 1 : 2);
  for (int i=priority; i <= lastPriority; i++) {
    if (offerQueue(i, e)) {
      return true;
    }
    // Update stats
    overflowedCalls.get(i).getAndIncrement();
  }
  return false;
}
