public static byte[] getClientId() {
  UUID uuid = UUID.randomUUID();
  ByteBuffer buf = ByteBuffer.wrap(new byte[BYTE_LENGTH]);
  buf.putLong(uuid.getMostSignificantBits());
  buf.putLong(uuid.getLeastSignificantBits());
  return buf.array();
}
