private String getString(Collection<String> strings) {
  StringBuilder sb = new StringBuilder(INITIAL_CAPACITY);
  boolean first = true;
  for(String str: strings) {
    if (!first) {
      sb.append(",");
    } else {
      first = false;
    }
    sb.append(str);
  }
  return sb.toString();
}
