protected boolean isBrowser(String userAgent) {
  if (userAgent == null) {
    return false;
  }
  for (Pattern pattern : browserUserAgents) {
    Matcher matcher = pattern.matcher(userAgent);
    if (matcher.matches()) {
      return true;
    }
  }
  return false;
}
