private static int unsignedBytesToInt(byte[] buf) {
  if (buf.length != 4) {
    throw new IllegalArgumentException(
        "Cannot handle byte array other than 4 bytes");
  }
  int result = 0;
  for (int i = 0; i < 4; i++) {
    result <<= 8;
    result |= ((int) buf[i] & 0xff);
  }
  return result;
}

