public static boolean isLocalhost(String host) {
    host = host != null ? StringUtils.toLowerCase(host.trim()) : "";
    if (host.startsWith("::1")) {
        int x = host.lastIndexOf('%');
        if (x >= 0) {
            host = host.substring(0, x);
        }
    }
    int x = Arrays.binarySearch(LOCALHOSTS, host);
    return x >= 0;
}

