public void run() {
  while (running) {
    try {
      Thread.sleep(reloadInterval);
    } catch (InterruptedException e) {
      //NOP
    }
    if (running && needsReload()) {
      try {
        trustManagerRef.set(loadTrustManager());
      } catch (Exception ex) {
        LOG.warn("Could not load truststore (keep using existing one) : " +
                 ex.toString(), ex);
      }
    }
  }
}
