WithName getPriorWithName(WithName post) {
  int i = Collections.binarySearch(withNameList, post, WITHNAME_COMPARATOR);
  if (i > 0) {
    return withNameList.get(i - 1);
  } else if (i == 0 || i == -1) {
    return null;
  } else {
    return withNameList.get(-i - 2);
  }
}
