private void identifierToString(StringBuilder buffer) {
  T id = null;
  try {
    id = decodeIdentifier();
  } catch (IOException e) {
    // handle in the finally block
  } finally {
    if (id != null) {
      buffer.append("(").append(id).append(")");
    } else {
      addBinaryBuffer(buffer, identifier);
    }
  }
}
