public static String getParameter(HttpServletRequest request, String name)
    throws IOException {
  List<NameValuePair> list = URLEncodedUtils.parse(request.getQueryString(),
      UTF8_CHARSET);
  if (list != null) {
    for (NameValuePair nv : list) {
      if (name.equals(nv.getName())) {
        return nv.getValue();
      }
    }
  }
  return null;
}
