private String getName2IdCmdLinux(final String name, final boolean isGrp) {
  String cmd;
  if (isGrp) {
    cmd = "getent group " + name + " | cut -d: -f1,3";   
  } else {
    cmd = "id -u " + name + " | awk '{print \"" + name + ":\"$1 }'";
  }
  return cmd;
}
