private String getId2NameCmdLinux(final int id, final boolean isGrp) {
  String cmd = "getent ";
  cmd += isGrp? "group " : "passwd ";
  cmd += String.valueOf(id) + " | cut -d: -f1,3";
  return cmd;
}
