private String getName2IdCmdMac(final String name, final boolean isGrp) {
  String cmd;
  if (isGrp) {
    cmd = "dscl . -read /Groups/" + name;
    cmd += " | grep PrimaryGroupID | awk '($1 == \"PrimaryGroupID:\") ";
    cmd += "{ print \"" + name + "  \" $2 }'";
  } else {
    cmd = "id -u " + name + " | awk '{print \"" + name + "  \"$1 }'";
  }
  return cmd;
}
