    subject.getPrivateCredentials(Credentials.class);
  if (!credentialsSet.isEmpty()){
    credentials = credentialsSet.iterator().next();
  } else {
    credentials = new Credentials();
    subject.getPrivateCredentials().add(credentials);
  }
  return credentials;
}

